#include <bits/stdc++.h>using namespace std; #define int long long int#define all(x) x.begin(), x.end() const int N = 5005;int a[N]; struct fenwick{    int ft[N];     void start(int n){        for(int i = 0; i < n + 5; i++) ft[i] = 0;    }     void up(int p, int x){        for(; p < N; p += p & -p) ft[p] += x;    }     int sum(int l, int r){        int ret = 0;        for(--l; l > 0; l -= l & l) ret -= ft[l];        for(; r > 0; r -= r & -r) ret += ft[r];        return ret;    }}FT; void solve(){    int n; cin >> n;    for(int i = 1; i <= n; i++) cin >> a[i];    FT.start(n);    int ans = 0;     for(int i = 1; i <= n; i++){        int s = 0;        for(int j = i + 1; j <= n; j++){            if(a[i] > a[j]) ans += s;            s += FT.sum(1, a[j]);        }        FT.up(a[i], 1);    }     cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--) solve();    return 0;}