#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k; cin >> n >> k;    vector<int> a(n), b(n);    for(auto& x : a) cin >> x;    for(auto& x : b) cin >> x;    sort(all(a));    sort(all(b));     int ans = 0;    for(auto x : a){        int val = x;        int l = lower_bound(all(a), val) - a.begin();        l = n - l;        int r = lower_bound(all(b), val) - b.begin();        r = n - r;         if(r - l > k) continue;        ans = max(ans, r * val);    }     for(auto x : b){        int val = x;        int l = lower_bound(all(a), val) - a.begin();        l = n - l;        int r = lower_bound(all(b), val) - b.begin();        r = n - r;         if(r - l > k) continue;        ans = max(ans, r * val);    }     cout << ans << '\n';    } int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}