#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n;    cin >> n;    vector<int> a(n + 2);    for (int i = 1; i <= n; i++)        cin >> a[i];    int os = a[1], es = a[2];    int ans = 0;    int cnt = 0;    for (int i = 2; i <= n; i += 2)    {        cnt = max(0LL, a[i - 1] + a[i + 1] - a[i]);        ans += cnt;        a[i + 1] = max(0LL, a[i + 1] - cnt);    }    cout << ans << '\n';}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}