#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int k, x;    cin >> k >> x;    int kk = k + 1;    // to avoid RTE    __int128_t topowerkp1 = (1ULL << kk);    __int128_t ch = x;    __int128_t vn = topowerkp1 - ch;    vector<int> ans;    while (ch != vn)    {        if (ch > vn)        {            ans.push_back(2);            ch -= vn;            vn += vn;        }        else        {            ans.push_back(1);            vn -= ch;            ch += ch;        }    }    cout << ans.size() << endl;    for (int i = ans.size() - 1; i >= 0; i--)    {        cout << ans[i] << " ";    }    cout << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}