#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n;    cin >> n;    deque<int> a(n);    for (auto &x : a)        cin >> x;    int x = 1;    for (int i = 0; i < n; i++)    {        if (a.front() == x)        {            a.pop_front();            x++;        }        else if (a.back() == x)            a.pop_back(), x++;        else        {            cout << "NO" << endl;            return;        }    }     cout << "YES\n";}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}