#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k;    cin >> n >> k;    string s;    cin >> s;    vector<int> v(3, 0);    for (char c : s)    {        v[c - '0']++;    }    int lo = v[0];    int hi = v[0] + v[2];    int m = n - k;     string ans;    for (int i = 1; i <= n; i++)    {        int l = i - m;        int r = i - 1;         if (l > r)        {            ans += '-';        }        else if (hi < l || lo > r)        {            ans += '-';        }        else if (lo >= l && hi <= r)        {            ans += '+';        }        else        {            ans += '?';        }    }    cout << ans << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}