#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() basic_string<int>v[31][905],v1;void solve(){    int n,k;cin>>n>>k;    k=(n*n+n)/2-k;    if(v[n][k].empty())cout<<0<<endl;    else{        for(auto x:v[n][k])cout<<x<<" ";        cout<<endl;    }}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    for(int i=0;i<=30;i++){        for(int j=0;j<=900;j++)        if((i+j==0)||!v[i][j].empty())        for(int k=1;k+i<=30;k++){            v1={};            for(int l=1;l<=k;l++){                v1+=(i+l);                v[i+l][j+(l*l+l)/2]=v1+v[i][j];            }        }    }    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}