#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n;    cin >> n;    vector<int> v(n);    for (auto &x : v)        cin >> x;    int a = (count(all(v), -1) % 2 == 0) ? 0 : 2;    cout << count(all(v), 0) + a << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}