#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n;    cin >> n;    vector<int> a(n);    for (auto &x : a)        cin >> x;    int ans = 0;    sort(all(a));    for (int i = 0; i + 1 < n; i += 2)    {        ans = max(ans, abs(a[i] - a[i + 1]));    }    cout << ans << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}