#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k;    cin >> n >> k;    vector<int> a(n);    map<int, int> mp;    for (auto &x : a)    {        cin >> x;        mp[x]++;    }    int aa = mp[k];    int b = 0;    for (int i = 0; i < k; i++)    {        if (!mp[i])            b++;    }    cout << max(aa, b) << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}