#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, m;    cin >> n >> m;    vector<int> a(m);    for (int i = 0; i < m; i++)        cin >> a[i];     bool f = false;    if (m == 1)    {        cout << n - a[0] + 1 << endl;        return;    }    for (int i = 0; i < m - 1; i++)    {        if (a[i] >= a[i + 1])        {            f = true;            break;        }    }    if (f)        cout << 1 << endl;    else        cout << n - a[m - 1] + 1 << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}