#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k;    cin >> n >> k;    string s;    cin >> s;    int ans = 0;    bool f = true;    int cnt = 0;    for (int i = 0; i < n; i++)    {        if (s[i] == '1')        {            if (cnt >= k - 1 or f)                ans++;            cnt = 0;            f = false;        }        else            cnt++;    }    cout << ans << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}