#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n;    cin >> n;    deque<int> v(n);    for (int i = 0; i < n; i++)        cin >> v[i];    int mx = 0;    for (int i = 0; i < n; i++)    {        mx = max(mx,v[i]);        if (i & 1)            v[i] = max(v[i], mx);    }    int ans = 0;    // v.resize(n +);    v.push_front(1e10);    v.push_back(1e10);    // for (auto &x : v)cout << x << " ";cout << endl;    for (int i = 1; i <= n; i += 2)    {        if (v[i] >= v[i - 1])            ans += (v[i] - v[i - 1] + 1), v[i] = v[i - 1] - 1;        if (v[i] >= v[i + 1])            ans += (v[i] - v[i + 1] + 1), v[i] = v[i + 1] - 1;    }    cout << ans << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}