#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k;    cin >> n >> k;    k = n * n - k;    if (k == 1)    {        cout << "NO\n";        return;    }    cout << "YES\n";    for (int i = 1; i <= n; i++)    {        for (int j = 1; j <= n; j++)        {            if (k > 0)            {                k -= 1;                if (i == 1 && j == 1)                    cout << 'R';                else if (i == 1)                    cout << 'L';                else                    cout << 'U';            }            else            {                cout << 'D';            }                }        cout << '\n';    }}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}