#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() int yee(int *a, int n, int *b){    for (int i = 0; i < n - 1; i++)    {        b[i + 1] = (b[i] == 1) - (a[i + 1] - a[i]);        if (b[i + 1] < 0 || b[i + 1] > 1)            return false;        b[i + 1] ^= 1;    }     int sum = 1;    for (int i = 1; i < n; i++)        sum += b[i] == 0;    return sum == a[0];} void solve(){    int n;    cin >> n;    int a[n];    for (int i = 0; i < n; i++)        cin >> a[i];     int b[n];    int ans = 0;     b[0] = 0;    ans += yee(a, n, b);    b[0] = 1;    ans += yee(a, n, b);     cout << ans << '\n';}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}