#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k;    cin >> n >> k;    vector<int> a(n);    for (int i = 0; i < n; i++)        cin >> a[i];    map<int, int> mp;    for (auto x : a)        mp[x]++;    vector<int> pre(n + 1, 0);    for (int i = 0; i < n; i++)        pre[i + 1] = pre[i] + mp[i + 1];    int ans = 1;    for (int i = n - 1; i >= 0; i--)    {        int x = 2LL * (i + 1);        int up = min(1LL * n, 2LL * x - 1);        int tot = pre[up];        int good = 0;        for (int m = i + 1; m <= 3 * (i + 1) && m <= n; m += (i + 1))            if (mp.count(m))                good += mp[m];        int bad = tot - good;        if (bad <= k)        {            ans = i + 1;            break;        }    }    cout << ans << '\n';}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}