#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k;    cin >> n >> k;     vector<int> a(n + 1);    vector<int> cnt(4 * n + 5, 0);    vector<int> pre(4 * n + 5, 0);     for (int i = 1; i <= n; i++)    {        cin >> a[i];        cnt[a[i]]++;    }     for (int i = 1; i <= 4 * n; i++)        pre[i] = pre[i - 1] + cnt[i];     int ans = 0;    for (int i = 1; i <= n; i++)    {        int x = pre[4 * i - 1] - cnt[i] - cnt[2 * i] - cnt[3 * i];        if (x <= k)            ans = i;    }     cout << ans << '\n';}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}