#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, x, y; cin >> n >> x >> y;    string s; cin >> s;    x = abs(x);    y = abs(y);        int cnt = 0;    for(auto x : s){        if(x == '4') cnt++;        else cnt += 2;    }     if(x + y <= cnt and x <= n and y <= n) cout << "YES\n";    else cout << "NO\n";} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}