#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    int l, r; cin >> l >> r;    int a[n];    for(int i = 0; i < n; i++) cin >> a[i];    sort(a, a + n);     int mid = a[n / 2];    if(mid < l) mid = l;    if(mid > r) mid = r;        int ans = 0;    for(int i = 0; i < n; i++) ans += abs(a[i] - mid);     cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}