#include <bits/stdc++.h>using namespace std; #define int long long#define endl '\n'#define all(x) x.begin(), x.end() void solve() {    int n; cin >> n;    map<int,int> mp;    vector<int> a(2*n);    for (int i = 0; i < 2*n; i++) {        cin >> a[i];        mp[a[i]]++;    }     int ans = 0;    if (mp.size() == n) {        ans += n;        for (auto [f, s] : mp) {            s--;            if (s & 1) ans++;        }        cout << ans << endl;        return;    }     if ((int)mp.size() == 1) {        int x = 0;        for (auto [f, s] : mp) x = s / 2;        cout << ((x & 1) ? 2 : 0) << endl;        return;    }     int bi = 0, jor = 0;     for (auto &[f, s] : mp) {        if (s & 1) bi++;        else jor++;    }     if (bi > 0) {                             int u = min(jor, n);        cout << min(2*n, bi + 2*u) << '\n';    } else {                                 int u = min(jor, n);        if ((n - u) & 1) u--;        cout << (u << 1LL) << '\n';    } } int32_t main() {    ios_base::sync_with_stdio(false);    cin.tie(nullptr);    int t;     cin >> t;    while (t--) solve();    return 0;}