#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k; cin >> n >> k;    int a[n], b[n];    for(int i = 0; i < n; i++) cin >> a[i];    for(int i = 0; i < n; i++) cin >> b[i];     int sum1 = 0, sum2 = 0, ans1 = -1e18, ans2 = -1e18;    for(int i = 0; i < n; i++){        sum1 += a[i];        sum2 += a[i];        sum1 = max(sum1, a[i]);        sum2 = max({sum2, sum1 + b[i], a[i]});        ans1 = max(ans1, sum1);        ans2 = max(ans2, sum2);    }    if(k & 1) cout << ans2 << '\n';    else cout << ans1 << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}