#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k; cin >> n >> k;    vector<int> a(n), b(n);    for (int & x : a) cin >> x;    for (int & x : b) cin >> x;     int cur = a[0], mx = a[0];    vector<int> L(n), R(n);    L[0] = cur;    for (int i = 1; i < n; ++i)    {        if (cur > 0)            cur += a[i];        else            cur = a[i];        L[i] = cur;        if (cur > mx)            mx = cur;    }     cur = a[n - 1];    R[n - 1] = cur;    for (int i = n - 2; i >= 0; --i)    {        if (R[i + 1] > 0)            cur = a[i] + R[i + 1];        else            cur = a[i];        R[i] = cur;    }     if ((k & 1) == 0)    {        cout << mx << '\n';        return;    }     int ans = mx;    for (int i = 0; i < n; ++i)    {        int val = L[i] + R[i] - a[i] + b[i];        if (val > ans)            ans = val;    }    cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}