#include <bits/stdc++.h>using namespace std;#define int long long#define endl '\n' int ans[100005];map<int,int> mp; int aww(int i){    if(ans[i]) return ans[i];    return ans[i] = aww(mp[i]);} void solve() {    int n;    cin >> n;    for(int i = 1; i <= n; i++) ans[i] = 0;     mp.clear();     vector<int> v(n + 1);    for(int i = 1; i <= n; i++) cin >> v[i];    queue<int> q;    for(int i = 1; i <= n; i++){        int x = v[i - 1] + i;        mp[i] = x - v[i];        if(mp[i] == 0) q.push(i);    }    int val = n;    while(!q.empty()){        int it = q.front(); q.pop();        ans[it] = val--;    }    for(int i = 1; i <= n; i++){        if(!ans[i]) aww(i);    }    for(int i = 1; i <= n; i++) cout << ans[i] << " ";    cout << endl;} int32_t main() {    ios_base::sync_with_stdio(0);    cin.tie(0);    int t;    cin >> t;    while(t--) solve();}