#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n;    cin >> n;    // string s = "";    // int temp = n;    // while (temp > 0)    // {    //     s = char('0' + (temp % 2)) + s;    //     temp /= 2;    // }     // while (!s.empty() && s.back() == '0')    // {    //     s.pop_back();    // }    if (n == 0)    {        cout << "YES" << endl;        return;    }    string s = bitset<64>(n).to_string();    s = s.substr(s.find('1'));     while (!s.empty() && s.back() == '0')        s.pop_back();     string ss = s;    reverse(all(ss));     if (ss != s)    {        cout << "NO" << endl;        return;    }     if (s.size() % 2 and s[s.size() / 2] == '1')    {        cout << "NO" << endl;    }    else    {        cout << "YES" << endl;    }} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}