#include <bits/stdc++.h>using namespace std; #define int long long int// #define endl '\n'#define all(x) x.begin(), x.end() int qr(vector<int> &a){    cout << "? ";    cout << a.size() << " ";    for (auto &it : a)        cout << it << " ";    cout << endl;    int x;    cin >> x;    return x;} void solve(){    int n;    cin >> n;    vector<int> ans(2 * n + 1, 0), v = {1};    for (int i = 2; i <= 2 * n; i++)    {        v.push_back(i);        int x = qr(v);        if (x != 0)        {            ans[i] = x;            v.pop_back();        }    }    vector<int> done;    for (int i = 1; i <= 2 * n; i++)    {        if (ans[i] != 0)            done.push_back(i);    }    for (auto &in : v)    {        done.push_back(in);        ans[in] = qr(done);        done.pop_back();    }    cout << "! ";    for (int i = 1; i <= 2 * n; i++)        cout << ans[i] << " ";    cout << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}