#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    string s; cin >> s;    reverse(all(s));    int ans = 0;     for(int i = 0; i + 1 < n; i++){        if(s[i] != s[i + 1]) ans++, s[i + 1] = s[i];    }    cout << ans << '\n';}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}