#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    int a[n + 2];    for(int i = 1; i <= n; i++) cin >> a[i];    a[n + 1] = a[1];        int ans = 0;    for(int i = 1; i <= n; i++) ans += max(a[i], a[i + 1]);     ans -= *max_element(a + 1, a + n + 1);     cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}