#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end()const int mod = 998244353; struct Mint{    int value;    static const int mod_value = mod;     Mint(long long v = 0)    {        value = v % mod;        if (value < 0)            value += mod;    }    Mint(long long a, long long b) : value(0)    {        *this += a;        *this /= b;    }     Mint &operator+=(Mint const &b)    {        value += b.value;        if (value >= mod)            value -= mod;        return *this;    }    Mint &operator-=(Mint const &b)    {        value -= b.value;        if (value < 0)            value += mod;        return *this;    }    Mint &operator*=(Mint const &b)    {        value = (long long)value * b.value % mod;        return *this;    }     friend Mint powermod(Mint a, long long e)    {        Mint res = 1;        while (e)        {            if (e & 1)                res *= a;            a *= a;            e >>= 1;        }        return res;    }    friend Mint inverse(Mint a) { return powermod(a, mod - 2); }     Mint &operator/=(Mint const &b) { return *this *= inverse(b); }    friend Mint operator+(Mint a, Mint const b) { return a += b; }    friend Mint operator-(Mint a, Mint const b) { return a -= b; }    friend Mint operator-(Mint const a) { return 0 - a; }    friend Mint operator*(Mint a, Mint const b) { return a *= b; }    friend Mint operator/(Mint a, Mint const b) { return a /= b; }    friend std::ostream &operator<<(std::ostream &os, Mint const &a) { return os << a.value; }    friend bool operator==(Mint const &a, Mint const &b) { return a.value == b.value; }    friend bool operator!=(Mint const &a, Mint const &b) { return a.value != b.value; }}; void solve(){    int n; cin >> n;    int a[n];    map<int, int> mp;    for(int i = 0; i < n; i++){        cin >> a[i];        mp[a[i]]++;    }     Mint dp[n + 1];    dp[0] = 1;    int mx = 0;    for(auto & [f, s] : mp){        for(int i = n; i >= s; i--){            dp[i] += s * dp[i - s];            mx = max(mx, s);        }    }     cout << accumulate(dp + mx, dp + n + 1, Mint(0LL)) << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}