#include <bits/stdc++.h>using namespace std;#define int long long int#define all(x) x.begin(), x.end()Â void solve(){    int n, q; cin >> n >> q;    int a[n];    for (int i = 0; i < n; i++) cin >> a[i];    sort(a, a + n);    reverse(a, a + n);    while (q--) {        int c; cin >> c;        int k = 0, ans = 0;        multiset<int, greater<int>> st;        int len = min(n, 30LL);        for (int i = 0; i < len; i++) st.insert(a[i]);        for (int i = 29; i >= 0 and st.size() and *st.begin() < c; i--) {            if(!(c & (1LL << i))) continue;            int top = *st.begin();            st.erase(st.begin());            c -= (1LL << i);            if (top & (1LL << i)) {                st.insert(top - (1LL << i));                continue;            }            ans += ((1LL << i) - top);        }        if (st.size() and *st.begin() >= c) c = 0;        ans += c;        cout << ans << '\n';    }} int32_t main(){    ios_base::sync_with_stdio(0); cin.tie(0);    int t = 1; cin >> t;    while (t--) solve();    return 0;}