#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n;    cin >> n;    int a[n];    int x = 0, y = 0;    for (int i = 0; i < n; i++)    {        cin >> a[i];        if (a[i] % 2)            x++;        else            y++;    }    if (x == 0 or y == 0)    {        for (int i = 0; i < n; i++)            cout << a[i] << " ";        cout << endl;    }    else    {        sort(a, a + n);        for (int i = 0; i < n; i++)            cout << a[i] << " ";        cout << '\n';    }}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}