#include <bits/stdc++.h>using namespace std; #define int long long#define endl '\n'#define all(x) x.begin(), x.end() const int LIM = 205;int pr[LIM + 1]; void sieve(){     for (int i = 0; i <= LIM; i++)        pr[i] = 1;    pr[0] = pr[1] = 0;    for (int i = 2; i * i <= LIM; i++)    {        if (pr[i])        {            for (int j = i * i; j <= LIM; j += i)            {                pr[j] = 0;            }        }    }} void solve(){    int n;    cin >> n;    vector<int> a(n);    int gc = 0;    for (int i = 0; i < n; i++)    {        cin >> a[i];        gc = gcd(a[i], gc);    }    for (int p = 2; p <= LIM; p++)    {        if (pr[p])        {            if (gc % p != 0)            {                cout << p << endl;                return;            }        }    }    cout << -1 << endl;} int32_t main(){    ios::sync_with_stdio(false);    cin.tie(nullptr);    sieve();    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}