#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n;    cin >> n;    int a[n + 1], b[n + 1], dp[n + 1];    for (int i = 1; i <= n; i++)        cin >> a[i];    for (int i = 1; i <= n; i++)        cin >> b[i];    for (int i = 1; i <= n; i++)    {        dp[i] = b[i];        for (int j = 1; j < i; j++)        {            if (a[j] > a[i])                continue;            dp[i] = max(dp[i], dp[j] + b[i]);        }    }    cout << accumulate(b + 1, b + n + 1, 0LL) - *max_element(dp + 1, dp + n + 1) << endl;}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}