#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    //int n; cin >> n;    string s; cin >> s;    int n = s.size();     if(n == 1){        cout << 1 << '\n';        return;    }    for(int i = 0; i + 1 < n; i++){        if(s[i] == '*' and s[i + 1] == '*'){            cout << -1 << '\n';            return;        }    }    for(int i = 0; i + 1 < n; i++){        if(s[i] == '*' and s[i + 1] == '<'){            cout << -1 << '\n';            return;        }    }    for(int i = 0; i + 1 < n; i++){        if(s[i] == '>' and s[i + 1] == '*'){            cout << -1 << '\n';            return;        }    }    for(int i = 0; i + 1 < n; i++){        if(s[i] == '>' and s[i + 1] == '<'){            cout << -1 << '\n';            return;         }    }     int ans = 0;    int cnt = 0;    // for(int i = 0; i + 1 < s.size(); i++){    //     cnt++;    //     if(s[i] != s[i + 1] and s[i] != '*' and s[i + 1] != '*'){    //         ans = max(ans, cnt);    //         cnt = 0;    //     }    //     else if( s[i] != s[i + 1] and (s[i] == '*' or s[i + 1] == '*')){    //         ans = max(ans, cnt + 1);    //         cnt = 0;    //     }    // }    for(int i = 0; i < n; i++){        if(s[i] == '<' or s[i] == '*') ans++;        else break;    }    for(int i = n - 1; i >= 0; i--){        if(s[i] == '>' or s[i] == '*') cnt++;    }    //cout << ans << " " << cnt << '\n';    cout << max(ans, cnt) << '\n';}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}