#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int x, y, k; cin >> x >> y >> k;    if(y == 1 or k == 1000000000000){        cout << -1 << '\n';        return;    }     if(y > k){        cout << k << '\n';        return;    }     while(x--){        k += (k - 1)/ (y - 1);         if(k > 1e12){            cout << -1 << '\n';            return;        }    }     cout << k << '\n';}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}