#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k; cin >> n >> k;    multiset<int> s, t;    for (int i = 0; i < n; i++)    {        int x;        cin >> x;        s.insert(x);    }    for (int i = 0; i < n; i++)    {        int x;        cin >> x;        t.insert((k - x) / (x + 1));    }    int ans = 0;    for (auto x : s)    {        auto it = t.lower_bound(x);        if (it != t.end())        {            ans++;            t.erase(it);        }    }    cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}