#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    int a[n + 1], b[n + 1];    for(int i = 1; i <= n ; i++) cin >> a[i];    for(int i = 1; i <= n; i++) cin >> b[i];     int aj = 0, mai = 0;    for(int i = 1; i <= n; i++){        aj ^= a[i];        mai ^= b[i];    }        if(aj ^ mai == 0){        cout << "Tie" << '\n';    }     else{        int one = 0;        int pos = 0;        while(1LL << (one + 1) <= (aj ^ mai)) one++;        for(int i = 1; i <= n; i++){            int x = a[i] ^ b[i];            if(x & (1LL << one)){                pos = i;            }        }         if(pos & 1) cout << "Ajisai" << '\n';        else cout << "Mai" << '\n';    }}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}