#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    int a[n];    vector< pair < int, int >> vp;    int mn = 1e18;        for(int i = 0; i < n - 1; i++){         cin >> a[i];        mn = min(mn, a[i]);        vp.push_back({mn, n - i});      }    cin >> a[n-1];      for(auto [f, s] : vp){        if(f == s){            cout << "NO\n";            return;        }    }        cout << "YES\n";}int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}