#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n ; cin >> n;     vector<int> ans, bad;    for(int i = 1; i <= n; i++){        if(i % 2 == 0) ans.push_back(i);    }     for(int i = 1; i <= n; i++){        if(i % 3 == 0 and i % 2 != 0) ans.push_back(i);    }     for(int i = 1; i <= n; i++){        if(i % 2 != 0 and i % 3 != 0) bad.push_back(i);    }     // for(auto &x : ans) cout << x << " "; cout << '\n';    // for(auto &x : bad) cout << x << " "; cout << '\n';     for(int i = 0; i < n; i++){        if(i % 3 == 0 and !bad.empty()){            cout << bad.back() << " ";            bad.pop_back();        }        else{            cout << ans.back() << " ";            ans.pop_back();        }    }    cout << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}