#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    int a[n + 1], p[n + 1], s[n + 1];    for(int i = 1; i <= n; i++) cin >> a[i];     p[1] = a[1];    for(int i = 2; i <= n; i++) p[i] = __gcd(p[i - 1], a[i]);     s[n] = a[n];    for(int i = n - 1; i >= 1; i--) s[i] = __gcd(s[i + 1], a[i]);     int ans = 0;    for(int i = 1; i < n; i++) ans += min(p[i], s[i]);     cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    //cin >> t;    while (t--)        solve();    return 0;}