#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, k; cin >> n >> k;    string s; cin >> s;     vector<int> vis(n, 0);    int zeros = 0;     for (int i = 0; i < n; ++i)    {        if (s[i] == '0')            zeros++;    }     for (int i = 0; i < n; ++i)    {        if (s[i] == '1')        {            int r = min(n - 1, i + k);            for (int j = i; j <= r; ++j)            {                vis[j] = 1;            }        }    }     int bad = 0;    for (int i = 0; i < n; ++i)    {        if (s[i] == '0' && vis[i])        {            bad++;        }    }     cout << (zeros - bad) << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}