#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    vector<int> a(n), b(n);    for (int i = 0; i < n; ++i) cin >> a[i];    for (int i = 0; i < n; ++i) cin >> b[i];     int mn = 0, mx = 0;     for (int i = 0; i < n; ++i)    {        int redmn = mn - a[i];        int redmx = mx - a[i];         int bmn = b[i] - mx;        int bmx = b[i] - mn;         int mn2 = min(redmn, bmn);        int mx2 = max(redmx, bmx);         mn = mn2;        mx = mx2;    }     cout << mx << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}