#include <bits/stdc++.h>using namespace std; #define int long long#define endl '\n'#define all(x) x.begin(), x.end() void solve() {    int n, l, r; cin >> n >> l >> r;    vector<int> a(n + 1); a[0] = 0;     for (int i = 1; i <= n; i++) a[i] = i;    a[r] = a[l - 1];      vector<int> res(n + 1);    for (int i = 1; i <= n; i++) {        res[i] = a[i - 1] ^ a[i];        if (res[i] == 0)            res[i] = 1;    }     for (int i = 1; i <= n; i++) cout << res[i] << ' '; cout << '\n';} int32_t main() {    ios_base::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) solve();    return 0;}