#include <bits/stdc++.h>using namespace std;  #define int long long int#define endl '\n'#define all(x) x.begin(), x.end()  void solve(){    string s, t; cin >> s >> t;    int n = s.length();    int m = t.length();    int cnt[26] = {0};    for(auto x : t) cnt[x - 'a']++;    int dorkar[26] = {0};    for(auto x : s) dorkar[x - 'a']++;     for(int i = 0 ; i < 26; i++){        if(cnt[i] < dorkar[i]){            cout << "Impossible" << '\n';            return;        }    }     string ans;    int idx = 0;    for(int i = 0; i < m; i++){          for(int j = 0; j < 26; j++){            if(cnt[j] > 0){                bool ff = false;                if(idx < n and s[idx] - 'a' == j) ff = true;                 else if(cnt[j] > dorkar[j]) ff = true;                if(ff){                    ans += (char)('a' + j);                    cnt[j]--;                    if(idx < n and s[idx] - 'a' == j){                          idx++;                        dorkar[j]--;                    }                    break;                }            }        }    }     cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}