#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end()const int N = 370; // int a[500005];// int dp[N][N][N];// int cnt; void solve(){    int n, k;cin >> n >> k;    vector<int> vis(k + 1, 0);    vector<vector<int>> dp(k + 1, vector<int>(k + 1, 0));     for (int i = 0; i < n; i++) {        int x;        cin >> x;        x = min(x, k);         while (x > 0 && !vis[x]) {            vis[x] = 1;              for (int j = k - x; j >= 0; j--) {                for (int l = 0; l < x; l++) {                    dp[j + x][x] = max(dp[j + x][x],dp[j][l] + (n - i) * (x - l));                }            }            x--;        }    }     int ans = 0;    for (auto &row : dp)    for (auto &val : row)    ans = max(ans, val);     cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}