#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    vector<int> a(n);    vector<bool> vis(n + 1, true);    for(int i = 0; i < n; i++) cin >> a[i];     int ans = 0, mx = a[0];    for(int i = 1; i < n; i++){        if(a[i] < mx) ans++;        else mx = a[i];    }     cout << ans << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}