#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    string s; cin >> s;    if(count(all(s), '1') == n){        cout << 0 << '\n';        return;    }    int a = 0, b = 0, cnt = 0;    // for(int i = 0; i < n; i++){    //     if(s[i] == '1'){    //         cnt++;    //     }    //     else{    //         a = max(a, cnt);    //         cnt = 0;    //     }    // }    a = max(a, cnt);    cnt = 0;        for(int i = 0; i < n; i++){        if(s[i] == '0'){            cnt++;        }        else{            b = max(b, cnt);            cnt = 0;        }    }    b = max(b, cnt);    int pos = -1;    for(int i = 0; i < n; i++){        if(s[i] == '0') a++;        else{            pos = i;            break;        }    }    for(int i = n - 1; i > pos; i--){        if(s[i] == '0') a++;        else break;    }        cout << max(a, b) << '\n';} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}