#include <bits/stdc++.h>    using namespace std;    #define int long long int    #define all(x) x.begin(), x.end()Â     void solve()    {        int n; cin >> n;        vector<int> o, e;        for (int i = 0; i < n; i++) {            int x; cin >> x;            if (x & 1) o.push_back(x);            else e.push_back(x);        }        sort(all(e)); sort(all(o));        if (o.empty()) {            for (int i = 0 ; i < n; i++) cout << 0 << ' ';            cout << '\n';            return;        }        if (e.empty()) {            for (int i = 0; i < n; i++) {                if (i & 1) cout << 0 << ' ';                else cout << o.back() << ' ';            }            cout << '\n';            return;        }        int cur = o.back();        cout << cur << ' ';        int esum = 0;        reverse(all(e));        for (auto i : e) {            esum += i;            cout << cur + esum << ' ';        }        for (int i = 1; i < o.size(); i += 2) {            if (i + 1 < o.size()) {                cout << cur + esum - e.back() << ' ' << cur + esum << ' ';            }            else cout << 0 << ' ';        }        cout << '\n';    }        int32_t main()    {        ios_base::sync_with_stdio(0); cin.tie(0);        int t = 1; cin >> t;        while (t--) solve();        return 0;    }