#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n; cin >> n;    deque<int> dq; dq.push_back(0);    for (int i = 1; i <= n; i++){        int sz = dq.size();        for (int j = 0; j < sz; j++){            int x = dq.front();            dq.pop_front();            dq.push_back(x * 2 + 1);        }        for (int k = 0; k < (1LL << i); k += 2)            dq.push_back(k);    }    for (auto x : dq) cout << x << " "; cout << "\n";} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}