#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define all(x) x.begin(), x.end() void solve(){    int n, x, y; cin >> n >> x >> y;    int a[n];    string s; cin >> s;    for(int i = 0; i < n; i++) cin >> a[i];    int sum = accumulate(a, a + n, 0LL);    int zero = 0, one = 0;    for(int i = 0; i < n; i++){        if(s[i] == '0') zero += a[i] / 2 + 1;        else one += a[i] / 2 + 1;    }    if((x + y) < sum){        cout << "NO\n";        return;    }    if(x < zero or y < one){        cout << "NO\n";        return;    }    if((zero == 0 and y < x + n) or (one == 0 and x < y + n)) cout << "NO\n";    else cout << "YES\n";} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}