#include <bits/stdc++.h>using namespace std; #define int long long int#define endl '\n'#define aint(x) x.begin(), x.end() void solve(){    int n, x, y;    cin >> n >> x >> y;    string s; cin >> s;    int a[n], sum = 0;    for (int i = 0; i < n; i++)    {        cin >> a[i];        sum += a[i];    }    if (sum > x + y)    {        cout << "NO\n";        return;    }     int k = x + y - sum;    int lo = 0, hi = 0;    pair<int, int> cnt = {0, 0};    pair<int, int> cntB = {0, 0};     for (int i = 0; i < n; i++){        if (s[i] == '0'){            cnt.first++;            lo += a[i] / 2 + 1;            hi += a[i];            if (a[i] % 2 == 0)                cnt.second++;        }        else{            cntB.first++;            hi += (a[i] + 1) / 2 - 1;            if (a[i] % 2 == 0)                cntB.second++;        }    }     int minL = lo;    if (!cntB.first){        int r = k;        if (r > cnt.second){            r -= cnt.second;            minL += (r + 1) / 2;        }    }     int maxR = hi;    if (cnt.first){        maxR += k;    }    else{        int r = k;        int g = min(r, cntB.second);        r -= g;        g += r / 2;        maxR += g;    }     cout << (x >= minL && x <= maxR ? "YES" : "NO") << "\n";} int32_t main(){    ios_base::sync_with_stdio(0);    cin.tie(0);    int t = 1;    cin >> t;    while (t--)        solve();    return 0;}